import styled from 'styled-components';

export const Wrapper = styled.div` 
width: 250px;
display: flex;
flex-direction: column;
align-items: center;
background-color: white;
padding-top: 20px;
margin-bottom:20px;
font-size: 20px;
font-weight: bold;
border-radius: 10px;
box-shadow: 10px 10px 5px #a4aeb1;
`

export const UserImg = styled.img`
width: 100%;
border-radius: 50%;
`
