import styled from 'styled-components';

export const Wrapper = styled.section`
box-shadow: 10px 10px 5px #a4aeb1;
width: 370px;
border-radius: 10px;
display: flex;
flex-direction: column;
align-items: center;
background-color: #eeeeee;
margin: 0 auto;

`
export const Title = styled.h2`
color: gray;
`
