import userInfo from "../user.json"

export const DescriptionEl = ({username,tag,location,avatar,stats}) => {
    return(
        <div class="description">
        <img
          src={avatar}
          alt="User avatar"
          class="avatar"
        />
        <p class="name">{username}</p>
        <p class="tag">{tag}</p>
        <p class="location">{location}</p>
      </div>   
    )
}
