import styled from 'styled-components';

export const List = styled.ul`
  width: 350px;
  padding: 20px 10px;
  background-color: #d2d2d2;
  margin: 20px auto;
  border-radius: 10px;
`;
export const ListItem = styled.li`
  background-color: white;
  display: flex;
  margin: 20px 0;
  height: 50px;
  padding: 10px 0;
  justify-content: space-evenly;
  box-shadow: 5px 5px 5px #a4aeb1;
  border-radius: 10px;
`;
