import styled from 'styled-components';
const getColor = props =>{
    if(props === true){
        return 'green'
    }
    if(props === false){
return 'red'
    }
}

export const OnlineMark = styled.span`
background-color:${getColor};
height: 20px;
width: 20px;
`
