import styled from 'styled-components';
const getColor = props => {
  if (props.id === 'id-1') {
    return 'green';
  }

  if (props.id === 'id-2') {
    return 'blue';
  }
  if (props.id === 'id-3') {
    return 'yellow';
  }
  if (props.id === 'id-4') {
    return 'pink';
  }
  if (props.id === 'id-5') {
    return 'orange';
  }
};

export const ListItemDiv = styled.div`
  background-color: ${getColor};
  height: 50px;
  font-size: 20px;
  width: 100%;
`;
