import styled from 'styled-components';

export const List = styled.ul`
display: flex;
width: 350px;
padding: 0;
justify-content: space-evenly;
`
const getColor = props =>{
    switch (props.id){
        case '.id-1':
            return 'green';
        case 'id-2' :
            return 'blue';
        case 'id-3' :
            return 'yellow' ;
        case '.id-4'  :
            return 'pink'   ;
        case 'id-5'   :
            return 'orange'  
    }
}

export const ListItem = styled.li`
background-color:${getColor};`
