import styled from 'styled-components';

export const List = styled.ul`
display: flex;
width: 350px;
padding: 0;
justify-content: space-evenly;
`
const getColor = props =>{
   if(props === 'id-1'){
    return 'green';
   }

   if(props === 'id-2'){
            return 'blue';}
  if(props === 'id-3'){
            return 'yellow' ;}
   if(props === 'id-4'){
            return 'pink'   ;}
    if(props === 'id-1'){
            return 'orange'  
    }
}

export const ListItem = styled.li`
background-color:${getColor};`
