import styled from 'styled-components';
const getColor = props => {
  if (props.id === 'id-1') {
    return '#b9d8b9';
  }

  if (props.id === 'id-2') {
    return '#c2abc9';
  }
  if (props.id === 'id-3') {
    return '#e8e89c';
  }
  if (props.id === 'id-4') {
    return '#ff4667';
  }
  if (props.id === 'id-5') {
    return 'o#485aac';
  }
};

export const ListItemDiv = styled.div`
  background-color: ${getColor};
  height: 50px;
  font-size: 15px;
  width: 70px;
`;
