import styled from 'styled-components';

export const List = styled.ul`
display: flex;
width: 350px;
padding: 0;
justify-content: space-evenly;
`
const getColor = props =>{
    switch (props.label){
        case '.docx':
            return 'green';
        case '.pdf' :
            return 'blue';
        case '.mp3' :
            return 'yellow' ;
        case '.psd'  :
            return 'pink'   ;
        case '.pdf'   :
            return 'orange'  
    }
}
