import styled from 'styled-components';
export const ListItem = styled.li`
display: flex;
flex-direction: column;
align-items: center;
width: 100px;
height: 70px;
border: 1px solid #b0adad;
justify-content: center;
background-color: #e8e3e3;
border-radius: 10px;
`
export const Text = styled.span`
font-weight: bold;
`
